import React, { useState } from "react";
import Sidebar from "./Sidebar";
import Rubros from "./Rubros";

const Layout = ({ setCurrentModule, currentModule }) => {
      const [collapsed, setCollapsed] = useState(false);

  return (

    <div className="layout">
      <Sidebar collapsed={collapsed} setCollapsed={setCollapsed} />

      <main className="content" style={{ marginLeft: collapsed ? "60px" : "250px" }}>
        <header className="content-header">
          <h1 className="system-title">TSG System Gestión</h1>
          <p className="system-subtitle">La solución para tu negocio</p>
        </header>

        <div className="content-body">
          {currentModule === "Rubros" && <Rubros />}
        </div>
      </main>
    </div>
  );
};

export default Layout;
