// Rubros.jsx
import React, { useState, useMemo } from 'react';
import { FaEdit, FaPlus } from 'react-icons/fa';
import '../Styles/Rubros.css';

const Rubros = () => {
  const rubrosIniciales = Array.from({ length: 15 }, (_, i) => ({
    id: i + 1,
    codigo: `R${i + 1}`,
    descripcion: `Rubro ${i + 1}`,
    estado: i % 2 === 0 ? 'Activo' : 'Inactivo',
  }));

  const [rubros, setRubros] = useState(rubrosIniciales);
  const [filtro, setFiltro] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: 'codigo', direction: 'asc' });
  const [pagina, setPagina] = useState(1);
  const ITEMS_POR_PAGINA = 10;

  // Filtrado
  const rubrosFiltrados = useMemo(() => {
    return rubros.filter(r =>
      r.descripcion.toLowerCase().startsWith(filtro.toLowerCase())
    );
  }, [rubros, filtro]);

  // Ordenamiento
  const rubrosOrdenados = useMemo(() => {
    const sorted = [...rubrosFiltrados];
    if (sortConfig !== null) {
      sorted.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) return sortConfig.direction === 'asc' ? -1 : 1;
        if (a[sortConfig.key] > b[sortConfig.key]) return sortConfig.direction === 'asc' ? 1 : -1;
        return 0;
      });
    }
    return sorted;
  }, [rubrosFiltrados, sortConfig]);

  // PaginaciÃ³n
  const totalPaginas = Math.ceil(rubrosOrdenados.length / ITEMS_POR_PAGINA);
  const rubrosPagina = rubrosOrdenados.slice(
    (pagina - 1) * ITEMS_POR_PAGINA,
    pagina * ITEMS_POR_PAGINA
  );

  const requestSort = key => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') direction = 'desc';
    setSortConfig({ key, direction });
  };

  return (
    <div className="rubros-container">
      <div className="rubros-header">
        <input
          type="text"
          placeholder="Filtrar por descripciÃ³n"
          value={filtro}
          onChange={e => setFiltro(e.target.value)}
        />
        <button className="btn-nuevo">
          <FaPlus /> Nuevo
        </button>
      </div>

      <table className="rubros-table">
        <thead>
          <tr>
            <th style={{ display: 'none' }}>Id</th>
            <th onClick={() => requestSort('codigo')}>CÃ³digo</th>
            <th onClick={() => requestSort('descripcion')}>DescripciÃ³n</th>
            <th onClick={() => requestSort('estado')}>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {rubrosPagina.map(r => (
            <tr key={r.id}>
              <td style={{ display: 'none' }}>{r.id}</td>
              <td>{r.codigo}</td>
              <td>{r.descripcion}</td>
              <td>{r.estado}</td>
              <td>
                <button className="btn-editar">
                  <FaEdit />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="rubros-paginacion">
        <button onClick={() => setPagina(p => Math.max(p - 1, 1))} disabled={pagina === 1}>
          {'<'}
        </button>
        <span>{pagina} / {totalPaginas}</span>
        <button onClick={() => setPagina(p => Math.min(p + 1, totalPaginas))} disabled={pagina === totalPaginas}>
          {'>'}
        </button>
      </div>
    </div>
  );
};

export default Rubros;
